    private void txtLKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtLKeyReleased
        double LKR,USD;
        LKR=Double.parseDouble(txtL.getText());
        USD=LKR/181;
        txtU.setText(Double.toString(USD));
    }//GEN-LAST:event_txtLKeyReleased

