    private void txtUKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtUKeyReleased
        double LKR,USD;
        USD=Double.parseDouble(txtU.getText());
        LKR=USD*181;
        txtL.setText(Double.toString(LKR));
    }//GEN-LAST:event_txtUKeyReleased

